package Lab10;import java.util.InputMismatchException;import java.util.Scanner;/** * A class that sends and deciphers a received message. * * @author Anna Bieszczad * @version 4/3/2018 */public class PacketSwitchingDriver{    public static void main(String[] args)    {        Scanner keyboard = new Scanner(System.in);        System.out.println("Enter the message to be sent");        String message = keyboard.nextLine();        int packetLength = Message.SMALLEST_PACKET_LENGTH;        try        {            do{                System.out.println("Enter the packet size (no smaller than 3)");                packetLength = keyboard.nextInt();            }while (packetLength < Message.SMALLEST_PACKET_LENGTH);        }        catch (InputMismatchException ime)        {           System.out.println("Setting packetLength to " + Message.SMALLEST_PACKET_LENGTH);        }        Message myMessage = new Message(message,packetLength);        System.out.println("The original message is \"" + myMessage.getOriginalMessage() + "\"");        myMessage.send();        myMessage.receive();        myMessage.assemble();        System.out.println("The message assembled back is \"" + myMessage.getRetrievedMessage() + "\"");        System.out.println("\n*** Done ***");    }}